package basic;

public class Arithmatic_oprator {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		System.out.println("example of Arithmatic operator");
		int a=15;
		int b=5;
		int c=a+b;
		System.out.println(c);
		int d=a-b;
		System.out.println(d);
		int e=a*b;
		System.out.println(e);
		int f=a/b;
		System.out.println(f);
		System.out.println("these are the basic arithmatic operation using arithmatic operator");

	}

}
