
package basic;

public class class_creation {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub

	}
	class A
	{
		//data member
		int a=10;
		String s="Anjali";
		//user defined method
		public void abc()
		{
			System.out.println("this is normal method");
		}
		//Initializer method
		{
			System.out.println("welcome");
		}
		//default block
		{
			System.out.println("this is defualt block");
		}
		//constructor block
		A(){
			System.out.println("this is constructor block");
		}}
		//static 
		static {
			
		
			System.out.println("this static block");

		}
		
	}


